
public class Company {
	String name;
	int establishment;
	String location;
public Company() {
	// TODO Auto-generated constructor stub
}
public Company(String name, int establishment, String location) {
	super();
	this.name = name;
	this.establishment = establishment;
	this.location = location;
}
@Override
public String toString() {
	return "Company [name=" + name + ", establishment=" + establishment + ", location=" + location + "]";
}




	}
	

